// src/components/Todo.js
import React from 'react';
import axios from 'axios';
import { FaCheckCircle } from 'react-icons/fa';

const Todo = ({ todo, deleteTodo, refreshTodos }) => {
  const handleToggle = async () => {
    try {
      await axios.put(`http://localhost:8080/api/todos/${todo.id}`, {
        ...todo,
        completed: !todo.completed
      });
      refreshTodos(); // Refresh todos after successful update
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  return (
    <div className={`TodoItem ${todo.completed ? 'completed' : 'incomplete'}`}>
      <span>{todo.title}</span>
      <button onClick={() => deleteTodo(todo.id)}>Delete</button>
      {todo.completed ? (
        <FaCheckCircle className="green-tick" />
      ) : (
        <button onClick={handleToggle}>Mark Complete</button>
      )}
    </div>
  );
};

export default Todo;
